@import './variables.scss';

$desktop-font-base: 180px;
$mobile-font-base: 90px;
$body-border-base: 40px;

*:focus {
  outline: none;
}
body {
  margin: 0;
  color: $blue;
  min-height: 100vh;
  background: $white;
}

h1 {
  font-family: $heading-family;
}

h1.page-title {
  font-size: $mobile-font-base;
  margin-top: 0;
  margin: 0 0 1rem;
  @include respond ($screen-sm) {
    font-size: $desktop-font-base;
  }
}

.container {
  @include container();
}

.content {
  padding: 20px 0;
  @include respond($screen-sm) {
    padding: 30px 0;
  }

  &:after,
  &:before {
    display: block;
    width: 2px;
    height: 100vh;
    content: '';
    position: fixed;
    top: 0;
    pointer-events: none;
    background-color: $blue;
    z-index: 999;
  }
  &:before {
    left: 20px;
    @include respond($screen-sm) {
      left: 80px;
    }
  }
  &:after {
    right: 20px; 
    @include respond($screen-sm) {
      right: 80px;
    }
  }
}

footer {
  @include respond($screen-sm) {
    position: fixed;
    width: 0;
    height: 0;
    transform: rotate(90deg);
    top: 0;
    right: 0;
    text-align: center;
    overflow: visible;
    a {
      width: 100vh;
    }
  }
  a {
    padding: 12px 20px;
    text-align: center;
    display: block;
    font-family: $heading-family;
    font-size: $desktop-font-base / 4;
    color: $blue;
    text-decoration: none;
    &:focus {
      color: $orange;
      text-decoration: underline;
    }
  }
}

.header {
  z-index: 2;
  &__title {
    display: inline-block;
    font-size: $mobile-font-base / 1.5;
    @include respond ($screen-sm) {
      font-size: $desktop-font-base / 2;
    }
    @include respond ($screen-md) {
      font-size: $desktop-font-base;
    }

    margin: 0 0 1rem;
    .spacer {
      width: 10px;
      @include respond ($screen-sm) {
        width: 20px;
      }
    }
  }
  &__intro {
    margin-bottom: 5rem;
    margin-top: 0;
    line-height: 1.35;
    font-family: $copy-family;
    font-size: $mobile-font-base / 3;
    @include respond ($screen-sm) {
      font-size: $desktop-font-base / 4;
      max-width: 80%;
    }
    span {
      display: inline-block;
      color: $white;
      background-color: $blue;
      line-height: 1.2;
      padding: 0 6px;
      transform-origin: left;
      transition-property: transform, color;
    }
  }
}

.header__intro span {
  background-color: $blue;
}

.tab {
  &__item {
    @include container();
  }
  &__item-btn {
    width: 100%;
    font-size: $mobile-font-base / 3;
    letter-spacing: 2px;
    text-transform: uppercase;
    text-align: left;
    background-color: $white;
    color: $blue;
    border: 0;
    font-family: $heading-family;
    border-bottom: 1px solid $blue;
    cursor: pointer;
    padding: 14px 24px;
    @include respond ($screen-sm) {
      font-size: $desktop-font-base / 4;
      letter-spacing: 6px;
      padding: 20px 30px;

    }
  }
}
.tab__content {
  min-height: calc(100vh - 158px);
  box-sizing: border-box;
  transition: height .75s ease;
  overflow: hidden;
  &:focus {
    outline: 2px solid $orange;
  }
  @include respond ($screen-sm) {
    min-height: calc(100vh - 90px);
  }
}

.tab__content-wrap {
  padding: 14px 7px;

  @include respond ($screen-sm) {
    padding: 48px 24px;
  }
}

