  from { transform : translateY(0); }
  to   { transform : translateY( $offset ); }
}
.wavetext span {
  display: inline-block;
  // animation-duration: #{$duration}s;
  // animation-name: wave;
  // animation-iteration-count: 2;
  // animation-direction: alternate;
}

@for $i from 0 to $count {
  .wavetext :nth-child( #{$count}n + #{$i} ) {
      animation-delay : 
        -#{($count - $i) * 1.5 * $duration / $count }s;

  }
}

.tabs__flex-wrap {
  display: flex;
  justify-content: flex-end;
  flex-direction: column;
  @include respond ($screen-sm) {
    flex-direction: row;
  }
}

.tabs__nav {
  width: 100%;
  border-top: 2px solid $blue;
  border-bottom: 2px solid $blue;
  .container {
    margin-right: 0;
  }
  button {
    background: none;
    border: none;
    border-left: 2px solid $blue;
    padding: 13px 30px;
    font-family: $copy-family;
    font-size: $mobile-font-base / 5;
    letter-spacing: 1px;
    color: $blue;
    cursor: pointer;
    @include respond ($screen-sm) {
      font-size: $desktop-font-base / 10;
      padding: 18px 50px;
    }
    &:focus {
      background-color: $blue;
      outline: none;
      text-decoration: underline;
      color: $white;
    }
    &.active {
      background-color: $blue;
      color: $white;
      &:focus {
        background-color: $blue;
      }
    }
  }
}

.tab__content {
  padding: 20px;
  position: relative;
  @include respond ($screen-sm) {
    padding: 80px;
  }
}
.tabs__transition {
  width: calc(100% - 40px);
  height: 100vh;
  transform: scaleY(0);
  transform-origin: top center;
  transition: transform 400ms cubic-bezier(0.455, 0.03, 0.515, 0.955);
  background-color: $orange;
  position: absolute;
  top: 0;
  left: 20px;
  z-index: 9;
  @include respond($screen-sm) {
    width: calc(100% - 160px);
    left: 80px;
  }
  &:before {
    content: '';
    display: block;
    position: absolute;
    width: 100%;
    height: 100%;
    transform: scaleY(0);
    transform-origin: top center;
    transition: transform 400ms cubic-bezier(0.455, 0.03, 0.515, 0.955);
    transition-delay: 200ms;
    z-index: 10;
    top: 0;
    left: 0;
    background-color: $blue;
  }
}
.tabs__text {
  transition: opacity .5s ease;
  transition-delay: 400ms;
}
.my-node-enter {
  .tabs__transition {
    transform: scaleY(0);
    &:before {
      transform: scaleY(0)
    }
  }
  .tabs__text {
    opacity: 0;
  }
}
.my-node-enter-active {
  .tabs__transition {
    transform: scaleY(1);
    &:before {
      transform: scaleY(1)
    }

  }
  .tabs__text {
    opacity: 0;
  }
}
.my-node-exit {
  .tabs__transition {
    transform: scaleY(1);
    &:before {
      transform: scaleY(1)
    }
  }
  .tabs__text {
    opacity: 1;
  }
}
.my-node-exit-active {
  .tabs__transition {
    transform: scaleY(0);
    &:before {
      transform: scaleY(0)
    }
  }
  .tabs__text {
    opacity: 1;
  }

}

.my-node-enter-done {
  .tabs__text {
    opacity: 1;
  }
}
